SET TITLE =
#{title}, GNR = #{gnr}, DIRECTOR = #{director}, RUNNINGTIME =
#{runningtime}
, RESTRICT = #{restrict}, ONAIR = #{onair}
, POSTER =
#{poster}, IMG1 = #{img1}, IMG2 = #{img2}, IMG3 = #{img3},
IMG4 =
#{img4}, IMG5 = #{img5}
, CONTENT = #{content}, VOTE = #{vote}
WHERE
m_id = #{m_id}
