mapper.update:
UPDATE USERS 
   SET PASSWORD = #{password}, NAME = #{name}, NICKNAME = #{nickname}
	 , BIRTH = #{birth}, PHONE = #{phone}, ADD1 = #{add1}, ADD2 = #{add2}
	 , GENDER = #{gender}, GRADE = #{grade}, EMAIL = #{email}
 WHERE U_ID = #{u_id}

UPDATE USERS 
   SET  EMAIL = #{email}
 WHERE U_ID = #{u_id}
